import React, { useState } from "react";
import useInterval from "../utils/useInterval";
import { minutesToDuration, secondsToDuration} from "../utils/duration";
import PlayStop from "./PlayStop"
import Focus from "./Focus";
import Break from "./Break"

function Pomodoro() {
  const [focusDuration, setFocusDuration] = useState(25);
  const [isTimerRunning, setIsTimerRunning] = useState(false);
  const [breakDuration, setBreakDuration] = useState(5);
  const [timeRemaining, setTimeRemaining] = useState(25 * 60);
  const [mode, setMode] = useState("focus");
  const [isSessionActive, setIsSessionActive] = useState(false);

  useInterval(
    () => {
      if (timeRemaining === 0) {
          new Audio(`https://bigsoundbank.com/UPLOAD/mp3/1482.mp3`).play();
          const duration = mode === 'focus' ? breakDuration : focusDuration; 
          setTimeRemaining(duration * 60);
          setMode((prevMode) => (prevMode==='focus'?'break':'focus'))
          return;
      }
      setTimeRemaining((currentTimeRemaining) => currentTimeRemaining - 1);
    },
    isTimerRunning ? 1000 : null
  );
  let currentDuration = mode === 'focus'? focusDuration: breakDuration;
    let percent = (1 - timeRemaining / (currentDuration*60)) * 100
    if(!isSessionActive) {
        return null
    }

  function playPause() {
    if (!isSessionActive) {
      setIsSessionActive(true);
      setTimeRemaining(focusDuration * 60);
    }
    setIsTimerRunning((prevState) => !prevState);
  }

    const stopTimer = () => {
    setIsSessionActive(false);
    setIsTimerRunning(false);
    setMode("focus");
  };

  return (
    <div className="pomodoro">
      <div className="row">
      <Focus 
      setFocusDuration={setFocusDuration} 
      setBreakDuration={setBreakDuration}
      isSessionActive={isSessionActive}
      minutesToDuration={minutesToDuration}
      setIsSessionActive={setIsSessionActive}/>
      <Break 
      setFocusDuration={setFocusDuration} 
      setBreakDuration={setBreakDuration}
      isSessionActive={isSessionActive}
      minutesToDuration={minutesToDuration}
      setIsSessionActive={setIsSessionActive}/>
      </div>
      <PlayStop 
      playPause={playPause}
      stopTimer={stopTimer}
      isSessionActive={isSessionActive}
      isTimerRunning={isTimerRunning}
      />
      <div>
        <div className="row mb-2">
          <div className="col">
          <h2 data-testid="session-title">
            {mode === "focus"
              ? `Focusing for ${minutesToDuration(focusDuration)} minutes`
              : `On Break for ${minutesToDuration(breakDuration)} minutes`}
          </h2>
            <p className="lead" data-testid="session-sub-title">
              {secondsToDuration(timeRemaining)} remaining
            </p>
            <h3>{!isTimerRunning && isSessionActive ? "PAUSED" : null}</h3>
          </div>
        </div>
        <div className="row mb-2">
          <div className="col">
            <div className="progress" style={{ height: "20px" }}>
              <div
                className="progress-bar"
                role="progressbar"
                aria-valuemin="0"
                aria-valuemax="100"
                aria-valuenow={percent}
                style={{width: `${percent}%`}}
              />
            </div>
          </div>
        </div>
    </div>
    </div>
  );
}

export default Pomodoro;
