import React from 'react';

function ProgressBar({
    mode,
    focusDuration,
    restDuration,
    timeRemaining,
    isSessionActive
}) {
    let currentDuration = mode === 'focus'? focusDuration: restDuration;
    let percent = (1 - timeRemaining / (currentDuration*60)) * 100
    console.log(percent)
    if(!isSessionActive) {
        return null
    }
    return (
        <div className="progress" style={{ height: "20px" }}>
        <div
          className="progress-bar"
          role="progressbar"
          aria-valuemin="0"
          aria-valuemax="100"
          aria-valuenow={percent}
          style={{width: `${percent}%`}}
        />
      </div>
    )
}

export default ProgressBar;
